    </div>
    <Row type="flex">
      <Col :span="spanLeft" class="chat-user-menu vertical-spacing">
        <Menu
          theme="light" 
          width="auto" 
          :open-names="['serving', 'switching', 'waiting']"
          @on-select="openChatWindow"
        >
          <Submenu name="serving">
            <template slot="title">
                <div class="chat-menu-title">
                  
                </div>
                <Badge :count="allUnread" overflow-count="999"></Badge>
            </template>
            <MenuItem v-for="(user, index) in userList" v-if="user.status === 'serving'":key="user.userId" :name="user.userId">
              <div class="chat-menu-item">
                <Badge :count="user.unread" overflow-count="99">
                  <!-- TODO: get user avatar and put it into this -->
                  <Avatar shape="square" icon="person"/>
                </Badge>
              </div>
              <div class="chat-menu-item">
                {{ user.userId.length > 10 ? user.userId.slice(0, 10) + '...' : user.userId }} <br />
                {{ getLastChatRecord(user.userId) }}
              </div>
            </MenuItem>
          </Submenu>
          <Submenu name="switching">
            <template slot="title">
              
            </template>
            <MenuItem v-for="(user, index) in userList" v-if="user.status === 'switching'" :key="user.userId" :name="user.userId">
              <div class="chat-menu-item">
                <!-- TODO: get user avatar and put it into this -->
                <Avatar shape="square" icon="person"/>
              </div>
              <div class="chat-menu-item">
                {{ user.userId.length > 10 ? user.userId.slice(0, 10) + '...' : user.userId }}
              </div>
            </MenuItem>
          </Submenu>
          <Submenu name="waiting">
            <template slot="title">
              
            </template>
            <MenuItem v-for="(user, index) in userList" v-if="user.status === 'waiting'" :key="user.userId" :name="user.userId">
              <div class="chat-menu-item">
                <!-- TODO: get user avatar and put it into this -->
                <Avatar shape="square" icon="person"/>
              </div>
              <div class="chat-menu-item">
                {{ user.userId.length > 10 ? user.userId.slice(0, 10) + '...' : user.userId }}
              </div>
            </MenuItem>
          </Submenu>
        </Menu>
      </Col>

      <!-- chat window -->
      <Col :span="spanMiddle" class="chat-window vertical-spacing">
        <div v-show="isChatting">

          <!-- header of chat window-->
          <div class="chat-window-header">
            <div class="chat-window-title">
              {{ chatUserId.length > 15 ? chatUserId.slice(0, 15) + '...' : chatUserId }}
            </div>
            <div class="chat-window-title-actions">
              <Button type="text" @click="connectToOthers">
                <Icon type="share" size="28"></Icon>
              </Button>
              <Button type="text" @click="showInfo">
                <Icon type="more" size="28"></Icon>
              </Button>
              <Button type="text" @click="closeChatWindow">
                <Icon type="close-round" size="24"></Icon>
              </Button>
            </div>
          </div>

          <!-- main window of chat -->
          <div id="chat-window" class="chat-window-content">
            <div class="chat-window-get-history">
              <Spin v-show="isGettingHistoryRecord"></Spin>
              <a @click="getHistoryRecord(chatUserId)">
                <Icon type="clock"/>
              </a>
            </div>

            <ul>
              <li v-for="(singleRecord, index) in currentChatRecord" :key="index">
                <p class="chat-msg-time">
                  <span>{{ singleRecord.time.toLocaleTimeString('zh-Hans-CN') }}</span>
                </p>
                <div class="chat-msg-body" :class="[{'from-me': singleRecord.direction === 'out'}]">
                  <div class="chat-single-record-element" v-if="singleRecord.hasSent === false">
                    <Spin></Spin>
                  </div>
                  <div class="avatar chat-single-record-element">
                    <!-- TODO: get avatar and put it into this -->
                    <Avatar shape="square" icon="person" :src="singleRecord.direction === 'out' ? avatarUrl : ''"/>
                  </div>
                  <div class="content chat-single-record-element" :class="singleRecord.type" v-if="singleRecord.type === 'text'">
                    {{ singleRecord.msg }}
                  </div>
                  <div class="content chat-single-record-element" :class="singleRecord.type" v-if="singleRecord.type === 'image'">
                    <img 
                      class="chat-image" 
                      :src="singleRecord.compressedUrl" 
                      alt="" 
                      @click="showLargeImage(singleRecord.url)"
                    />
                  </div>
                  <div class="content chat-single-record-element" :class="singleRecord.type" v-if="singleRecord.type === 'file'"
                    @click="downloadFile(singleRecord.url, singleRecord.name)">
                    <div class="chat-file-prepend">
                      <svg class="chat-file-icon">
                        <use :xlink:href="getFileIconName(singleRecord.name)" />
                      </svg>
                    </div>
                    <div class="chat-file-info">
                      {{ singleRecord.name.length > 15 ? singleRecord.name.slice(0, 15) + '...' : singleRecord.name }} <br />
                      {{ singleRecord.size}}KB
                    </div>
                  </div>
                </div>
              </li>
            </ul>
          </div>

          <!-- emoji component by emoji-mart-->
          <div class="chat-emoji-panel" v-show="showEmojiPanel">
            <picker 
              ref="emoji-mart"
              native 
              :i18n="emojiLocalization" 
              :perLine="10"
              color="#2d8cf0"
              @click="insertEmoji"
            ></picker>
          </div>

          <!-- files uploaded and waiting for send -->
          <transition name="slide-fade">
            <div class="chat-window-upload-file-list" v-show="uploadList.length">
              <div class="upload-single-file" v-for="(file, index) in uploadList" :key="file.uid">
                <div class="list-file-element list-file-prepend">
                  <svg class="list-file-icon">
                    <use :xlink:href="getFileIconName(file.name)" />
                  </svg>
                </div>
                <div class="list-file-element list-file-name">
                  {{ file.name.length > 10 ? file.name.slice(0, 10) + '...' : file.name }}
                </div>
                <div class="upload-single-file-progress" v-show="file.status !== 'finished'">
                  <div class="upload-progress" v-if="file.showProgress">
                    <Progress :percent="file.percentage" hide-info status="active"></Progress>
                  </div>
                  <div class="upload-progress" v-else>
                    ...
                  </div>
                </div>
                <div class="upload-single-file-cover" @click="handleRemove(file)">
                  <Icon type="close-round" size="20"></Icon>
                </div>
              </div>
            </div>
          </transition>

          <!-- buttons like emoji or file -->
          <div class="chat-window-input-actions">
            <div class="chat-window-input-media">
              <div class="media-button">
                <Upload 
                  multiple
                  ref="upload"
                  :max-size="maxFileSize"
                  :show-upload-list="false"
                  :action="fileServerUrl"
                  :data="{validTime: 7}"
                  :before-upload="handleBeforeUpload"
                  :on-success="handleSuccess"
                  :on-error="handleError"
                  :on-exceeded-size="handleMaxSize">
                  <Button type="ghost" icon="document"></Button>
                </Upload>
              </div>
              <div class="media-button">
                <Button type="ghost" icon="happy" @click="() => {showEmojiPanel = !showEmojiPanel}"></Button>
              </div>
              <div class="media-button">
                <Button type="ghost" icon="monitor" @click="askForCapture"></Button>
              </div>
            </div>
            <div class="chat-window-input-send">
              <Button type="success" icon="paper-airplane" @click="sendMessage"></Button>
            </div>
          </div>

          <!-- text area input -->
          <div class="chat-window-input">
            <Input
              v-model="inputText" 
              type="textarea" 
              :rows="4" 
              placeholder="Ctrl+Enter" 
              @on-keyup.ctrl.enter="sendMessage"
              @on-focus="getFocus"
            ></Input>
          </div>

        </div>

        <!-- when there are not chatting user -->
        <div v-show="!isChatting" class="chat-empty">
           <br />
          Icon designed by Smashicons from Flaticon
        </div>

      </Col>
      <Col :span="spanRight" v-if="spanRight">
        
        
      </Col>
    </Row>
    <Modal v-model="showLargeImageModal" width="80%" title="">
      <div class="large-image">
        <img :src="largeImageSrc" />
      </div>
      <div slot="footer"></div>
    </Modal>
  </div>
</template>

<script>
import axios from 'axios'
export default {
  name: 'StaffChat',
  data () {
    return {
      spanLeft: 6,
      spanRight: 0,
      isChatting: false,
      isGettingHistoryRecord: false,
      showEmojiPanel: false,
      chatUserId: '',
      chatWindowScroll: {
        last_known_scroll_position: 0,
        ticking: false
      },
      inputText: '',
      inputCaretPos: 0,
      showLargeImageModal: false,
      largeImageSrc: '',
      uploadList: [],
      maxFileSize: 204800, // KB
      emojiLocalization: {
        search: '',
        notfound: '',
        categories: {
          search: '',
          recent: '',
          people: '',
          nature: '',
          foods: '',
          activity: '',
          places: '',
          objects: '',
          symbols: '',
          flags: '',
          custom: ''
        }
      },
      fileIconMap: {
        // compressed file
        'application/zip': '#zip-1',
        'application/x-zip-compressed': '#zip-1',
        'application/x-rar-compressed': '#zip-1',
        'application/x-7z-compressed': '#zip-1',
        // word
        'application/msword': '#doc',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document': '#doc',
        // powerpoint
        'application/vnd.ms-powerpoint': '#ppt',
        'application/vnd.openxmlformats-officedocument.presentationml.presentation': '#ppt',
        // excel
        'application/application/vnd.ms-excel': '#xls',
        'application/application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': '#xls',
        // javascript
        'application/javascript': '#javascript',
        // css
        'text/css': '#css',
        // mp3
        'audio/mpeg3': '#mp3',
        'audio/x-mpeg-3': '#mp3',
        // mp4
        'video/mpeg': '#mp4',
        'video/x-mpeg': '#mp4',
        // avi
        'application/x-troff-msvideo': '#avi',
        'video/avi': '#avi',
        'video/msvideo': '#avi',
        'video/x-msvideo': '#avi',
        // flash
        'application/x-shockwave-flash': '#fla'
      }
    }
  },
  computed: {
    spanMiddle () {
      return 24 - this.spanLeft - this.spanRight
    },
    chatRecordList () {
      return this.$store.state.chatRecordList
    },
    currentChatRecord () {
      return this.$store.state.chatRecordList[this.chatUserId]
    },
    userList () {
      return this.$store.state.userList
    },
    quickReplyList () {
      return this.$store.state.quickReplyList
    },
    allUnread () {
      let sum = 0
      for (let i = 0, len = this.userList.length; i < len; i++) {
        if (this.userList[i].status === 'serving') {
          sum += this.userList[i].unread
        }
      }
      return sum
    },
    socket () {
      return this.$store.state.socket
    },
    httpServerUrl () {
      return this.$store.state.httpServerUrl
    },
    fileServerUrl () {
      return this.$store.state.fileServerUrl
    },
    imageCompressUrl () {
      return this.$store.state.imageCompressUrl
    },
    staffId () {
      return this.$store.state.staffId
    },
    avatarUrl () {
      return this.$store.state.avatarUrl
    },
    readyToSend () {
      for (let file of this.uploadList) {
        if (file.status !== 'finished') {
          return false
        }
      }
      return true
    }
  },
  methods: {
    chatWindowScrollDown () {
      let element = document.getElementById('chat-window')
      if (element) {
        element.scrollTop = element.scrollHeight
      }
    },
    openChatWindow (userId) {
      this.isChatting = true
      this.chatUserId = userId
      // clear flag
      this.isGettingHistoryRecord = false
      // clear input
      this.inputText = ''
      // clear files
      this.$refs.upload.clearFiles()
      this.uploadList = this.$refs.upload.fileList
      // clear unread
      this.$store.commit({
        type: 'clearUserUnread',
        userId: this.chatUserId
      })
      console.log('Switch user: ' + this.chatUserId)
    },
    connectToOthers () {
      this.$Notice.warning({
        title: ''
      })
    },
    closeChatWindow () {
      // TODO: close chat with chatUserId
      this.isChatting = false
      this.chatUserId = ''
    },
    getLastChatRecord (userId) {
      let chatRecord = this.chatRecordList[userId]
      if (chatRecord !== undefined && chatRecord.length > 0) {
        let singleRecord = chatRecord[chatRecord.length - 1]
        if (singleRecord.type === 'text') {
          return singleRecord.msg.length > 8 ? singleRecord.msg.slice(0, 8) + '...' : singleRecord.msg
        } else if (singleRecord.type === 'image') {
          return '[]'
        } else if (singleRecord.type === 'file') {
          return '[]'
        }
      } else {
        return ''
      }
    },
    getHistoryRecord (userId) {
      this.isGettingHistoryRecord = true
      console.log('get history record of ' + userId)
      axios.get(this.httpServerUrl + '/chat-record', {
        params: {
          'userId': userId,
          'staffId': this.staffId,
          'index': -1
        }
      }).then(response => {
        console.log(response)
        this.isGettingHistoryRecord = false
      }).catch(error => {
        this.$Notice.error({
          title: ''
        })
        console.log(error)
        this.isGettingHistoryRecord = false
      })
    },
    showInfo () {
      this.spanRight = this.spanRight === 0 ? 3 : 0
      // TODO: Add detail info
    },
    showLargeImage (src) {
      this.showLargeImageModal = true
      this.largeImageSrc = src
    },
    downloadFile (url, name) {
      let downloadLink = document.createElement('a')
      downloadLink.download = name
      downloadLink.href = url
      downloadLink.click()
    },
    sendMessage () {
      if (!this.readyToSend) {
        this.$Notice.error({
          title: ''
        })
        return
      }
      let sendMsg = this.inputText
      if (sendMsg === '' && this.uploadList.length === 0) {
        this.$Notice.warning({
          title: ''
        })
        return
      }
      sendMsg = this.insertQuickReply(sendMsg)
      let date = new Date()
      // send text msg
      if (sendMsg !== '') {
        this.$store.commit({
          type: 'addChatRecord',
          userId: this.chatUserId,
          content: {
            'staffId': this.staffId,
            'userId': this.chatUserId,
            'direction': 'out',
            'msg': sendMsg,
            'type': 'text',
            // 'time': date.toLocaleTimeString('zh-Hans-CN')
            'time': date
            // 'hasSent': false
          }
        })
        this.socket.emit('staffMsg', {
          'staffId': this.staffId,
          'userId': this.chatUserId,
          'token': window.localStorage.getItem('token'),
          'msg': sendMsg,
          'type': 'text'
        })
        // clear input
        this.inputText = ''
      }
      // send files
      if (this.uploadList.length) {
        for (const index of this.uploadList.keys()) {
          const file = this.uploadList[index]
          let fileType = file.response.type.startsWith('image') ? 'image' : 'file'
          if (fileType === 'image') {
            this.$store.commit({
              type: 'addChatRecord',
              userId: this.chatUserId,
              content: {
                'staffId': this.staffId,
                'userId': this.chatUserId,
                'direction': 'out',
                'url': file.response.data,
                'compressedUrl': file.response.compressedUrl ? file.response.compressedUrl : file.response.data,
                'type': fileType,
                // 'time': date.toLocaleTimeString('zh-Hans-CN')
                'time': date
                // 'hasSent': false
              }
            })
            this.socket.emit('staffMsg', {
              'staffId': this.staffId,
              'userId': this.chatUserId,
              'token': window.localStorage.getItem('token'),
              'type': fileType,
              'url': file.response.data,
              'compressedUrl': file.response.compressedUrl ? file.response.compressedUrl : file.response.data
            })
          } else {
            this.$store.commit({
              type: 'addChatRecord',
              userId: this.chatUserId,
              content: {
                'staffId': this.staffId,
                'userId': this.chatUserId,
                'direction': 'out',
                'url': file.response.data,
                'name': file.name,
                'size': (file.size > 1024) ? (file.size >> 10) : 1,
                'mimeType': file.response.type,
                'type': fileType,
                // 'time': date.toLocaleTimeString('zh-Hans-CN')
                'time': date
                // 'hasSent': false
              }
            })
            this.socket.emit('staffMsg', {
              'staffId': this.staffId,
              'userId': this.chatUserId,
              'token': window.localStorage.getItem('token'),
              'type': fileType,
              'url': file.response.data,
              'name': file.name,
              'size': (file.size > 1024) ? (file.size >> 10) : 1,
              'mimeType': file.response.type
            })
          }
        }
        // clear files
        this.$refs.upload.clearFiles()
        this.uploadList = this.$refs.upload.fileList
      }
      // TODO: 
    },
    askForCapture () {
      this.$Notice.warning({
        title: ''
      })
    },
    handleBeforeUpload (file) {
      const check = this.uploadList.length < 5
      if (!check) {
        this.$Notice.warning({
          title: '5'
        })
      }
      return check
    },
    handleMaxSize (file) {
      this.$Notice.warning({
        title: '',
        desc: '  ' + file.name + '  ' + this.maxFileSize + ' KB.'
      })
    },
    handleRemove (file) {
      const fileList = this.$refs.upload.fileList
      this.$refs.upload.fileList.splice(fileList.indexOf(file), 1)
      this.$Notice.success({
        title: ' ' + file.name + ' '
      })
    },
    handleError (error, file) {
      this.$Notice.error({
        title: ' ' + file.name + ' ',
        desc: '' + error
      })
    },
    handleSuccess (response, file) {
      this.$Notice.success({
        title: ' ' + file.name + ' '
      })
      if (response.type.startsWith('image')) {
        console.log(response.data)
        axios.post(this.imageCompressUrl, {
          fileUrl: response.data
        }).then(response => {
          file.response['compressedUrl'] = response.data.data
          console.log(response)
        }).catch(error => {
          console.log('Get compressed image url failed!')
          console.log(error)
        })
      }
      console.log(this.uploadList)
    },
    getFocus () {
      this.showEmojiPanel = false
      // TODO: get the caret pos
    },
    insertEmoji (emoji, event) {
      // TODO: insert at caret pos
      this.inputText += emoji.native
    },
    insertQuickReply (str) {
      // change "# + quick phrase + whitespace" into long sentense
      let reg = new RegExp(/#.+?\s/, 'g')
      let result = str.replace(reg, (match, offset, string) => {
        let phrase = match.slice(1, -1)
        let pairResult = this.quickReplyList.find((value, index, arr) => {
          return value.phrase === phrase
        })
        if (pairResult !== undefined) {
          return pairResult.sentence
        } else {
          return match
        }
      })
      return result
    },
    getFileIconName (fileName) {
      let array = fileName.split('.')
      let suffix = array[array.length - 1]
      let map = new Map([
        // compressed file
        ['zip', '#zip-1'],
        ['rar', '#zip-1'],
        ['7z', '#zip-1'],
        // image
        ['jpg', '#jpg'],
        ['jpeg', '#jpg'],
        ['png', '#png'],
        // word
        ['doc', '#doc'],
        ['docx', '#doc'],
        // powerpoint
        ['ppt', '#ppt'],
        ['pptx', '#ppt'],
        // excel
        ['xls', '#xls'],
        ['xlsx', '#xls'],
        // pdf
        ['pdf', '#pdf'],
        // exe
        ['exe', '#exe'],
        // psd
        ['psd', '#psd'],
        // txt
        ['txt', '#txt'],
        // html
        ['html', '#html'],
        ['htm', '#html'],
        // javascript
        ['js', '#javascript'],
        // css
        ['css', '#css'],
        // json
        ['json', '#json-file'],
        // mp3
        ['mp3', '#mp3'],
        // mp4
        ['mp4', '#mp4'],
        // avi
        ['avi', '#avi'],
        // flash
        ['swf', '#fla']
      ])
      let iconName = map.get(suffix)
      if (iconName) {
        return iconName
      } else {
        return '#file'
      }
    }
  },
  updated () {
    this.chatWindowScrollDown()
  },
  created () {
    // update user queue
    axios.get(this.httpServerUrl + '/queue', {
      params: {
        staffId: window.localStorage.getItem('id'),
        token: window.localStorage.getItem('token')
      }
    }).then(response => {
      let body = response.data.data
      console.log(body)
      if (body.code === 0) {
        // successfully get user queue
        let arr = []
        for (let value of body.queue.serving) {
          if (value) {
            arr.push({
              userId: value,
              status: 'serving',
              unread: 0
            })
          }
        }
        for (let value of body.queue.waiting) {
          if (value) {
            arr.push({
              userId: value,
              status: 'waiting',
              unread: 0
            })
          }
        }
        this.$store.commit({
          type: 'refreshUserList',
          content: arr
        })
      } else {
        // failed to get user queue
        this.$Notice.error({
          title: ''
        })
      }
    }).catch(error => {
      console.log(error)
      this.$Notice.error({
        title: ''
      })
    })
  },
  mounted () {
    this.uploadList = this.$refs.upload.fileList
  }
}
</script>

