    </div>
    <div class="layout">
      <Row type="flex">
        <Col span="24">
        <div class="chat-header">
          <div class="chat-title chat-title-company-name">
            {{ companyName }}
          </div>
          <div v-if="staffNickName.length" class="chat-title chat-title-staff-type">
            {{ staffNickName }}（{{ staffRole }}）
          </div>
          <div v-else class="chat-title chat-title-staff-type">
            {{ staffRole }}
          </div>
        </div>

        <!-- chat records -->
        <div class="chat-content" id="user-chat-content">
          <div class="chat-window-get-history">
            <Spin v-show="isGettingHistoryRecord"></Spin>
            <a @click="getHistoryRecord()">
              <Icon type="clock"/>加载历史消息
            </a>
          </div>
          <ul>
            <li v-for="(singleRecord, index) in currentChatRecord">
              <p class="chat-msg-time">
                <span>{{ singleRecord.time }}</span>
              </p>
              <div class="chat-msg-body" :class="[{'from-me': singleRecord.from === userId}]">
                <div class="avatar chat-single-record">
                  <Avatar shape="square" icon="person"/>
                </div>
                <div v-if="singleRecord.type === 'text'" class="content chat-single-record">
                  {{ singleRecord.msg }}
                </div>
                <div v-else-if="singleRecord.type === 'image'" class="content chat-single-record" style="cursor: pointer">
                  <img class="chat-image" :src="singleRecord.compressedUrl" alt="聊天图片" @click="showLargeImage(singleRecord.url)">
                </div>
                <div v-else-if="singleRecord.type === 'file'" :class="singleRecord.type" @click="singleRecord.from === staffId ? downloadFile() : showLocalFile()" style="cursor: pointer" class="content chat-single-record">
                    <div class="chat-file-prepend">
                      <svg class="file-icon">
                        <use :xlink:href="getFileIconName(singleRecord.name)" />
                      </svg>
                    </div>
                    <div class="chat-file-info">
                      {{ singleRecord.name.length > 15 ? singleRecord.name.slice(0, 15) + '...' : singleRecord.name }} <br />
                      {{ singleRecord.size}}KB
                    </div>
                </div>
              </div>
            </li>
          </ul>
        </div>

        <!-- emoji panel -->
        <div class="chat-emoji-panel" v-show="showEmojiPanel">
          <picker
            ref="emoji-mart"
            native
            :i18n="emojiLocalization"
            :perLine="10"
            color="#2d8cf0"
            @click="insertEmoji"
          ></picker>
        </div>

        <!-- file to be uploaded -->
        <transition name="slide-fade">
          <div class="chat-window-upload-file-list" v-show="uploadList.length">
            <div class="upload-single-file" v-for="(file, index) in uploadList" :key="file.uid">
              <div class="list-file-element list-file-prepend">
                <svg class="list-file-icon">
                  <use :xlink:href="getFileIconName(file.name)" />
                </svg>
              </div>
              <div class="list-file-element list-file-name">
                {{ file.name.length > 15 ? file.name.slice(0, 15) + '...' : file.name }}
              </div>
              <div class="upload-single-file-progress" v-show="file.status !== 'finished'">
                <div class="upload-progress" v-if="file.showProgress">
                  <Progress :percent="file.percentage" hide-info status="active"></Progress>
                </div>
                <div class="upload-progress" v-else>
                  正在上传...
                </div>
              </div>
              <div class="upload-single-file-cover" @click="handleRemove(file)">
                <Icon type="close-round" size="20"></Icon>
              </div>
            </div>
          </div>
        </transition>

        <!-- input buttons -->
        <div class="chat-input-actions">
          <div class="chat-window-input-media">
            <div class="media-button">
              <Upload
                multiple
                ref="upload"
                :max-size="maxFileSize"
                :show-upload-list="false"
                :action="fileServerUrl"
                :data="{validTime: 7}"
                :before-upload="handleBeforeUpload"
                :on-success="handleSuccess"
                :on-error="handleError"
