            <Button type="success" icon="paper-airplane" @click="sendMessage">发送</Button>
          </div>
        </div>
        <div class="chat-input">
          <Input v-model="inputText" type="textarea" :rows="8" placeholder="在这里输入信息，按Ctrl+Enter发送" @on-keyup.ctrl.enter="sendMessage"></Input>
        </div>
        </Col>
      </Row>
      <Modal v-model="showLargeImageModal" width="80%" title="查看图片">
        <div class="large-image">
          <img :src="largeImageSrc" />
        </div>
        <div slot="footer"></div>
      </Modal>
      <Modal
        v-model="showAskScreenShotModal"
        width="80%"
        title="请求截图"
        @on-ok="screenShot"
        ok-text="允许"
        @on-cancel="showAskScreenShotModal = false"
        cancel-text="拒绝">
        客服向您请求截图
      </Modal>
      <Modal
        v-model="showScreenShotModal"
        width="80%"
        title="屏幕截图"
        @on-ok="sendScreenShot"
        ok-text="发送"
