<script>
  import axios from 'axios'
  import html2canvas from 'html2canvas'
  export default {
    name: 'UserChat',
    data () {
      return {
        companyName: '',
        staffNickName: '',
        staffPicUrl: '',
        staffRole: '',
        inputText: '',
        earlistRecordIndex: '',
        showEmojiPanel: false,
        showLargeImageModal: false,
        largeImageSrc: '',
        showAskScreenShotModal: false,
        showScreenShotModal: false,
        screenShotSrc: '',
        isGettingHistoryRecord: false,
        isScrollDown: false,
        cachedMsg: {},
        uploadList: [],
        maxFileSize: 204800, // KB
        emojiLocalization: {
          search: '搜索',
          notfound: '没有找到表情',
          categories: {
            search: '搜索结果',
            recent: '常用',
            people: '表情符号与人物',
            nature: '动物与自然',
            foods: '食物与饮料',
            activity: '活动',
            places: '旅行与地点',
            objects: '物体',
            symbols: '符号',
            flags: '旗帜',
            custom: '自定义'
          }
        },
        imgFileIndex: -1,
        fileIconMap: {
          // compressed file
          'application/zip': '#zip-1',
          'application/x-zip-compressed': '#zip-1',
          'application/x-rar-compressed': '#zip-1',
          'application/x-7z-compressed': '#zip-1',
          // word
          'application/msword': '#doc',
          'application/vnd.openxmlformats-officedocument.wordprocessingml.document': '#doc',
          // powerpoint
          'application/vnd.ms-powerpoint': '#ppt',
          'application/vnd.openxmlformats-officedocument.presentationml.presentation': '#ppt',
          // excel
          'application/application/vnd.ms-excel': '#xls',
          'application/application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': '#xls',
          // javascript
          'application/javascript': '#javascript',
          // css
          'text/css': '#css',
          // mp3
          'audio/mpeg3': '#mp3',
          'audio/x-mpeg-3': '#mp3',
          // mp4
          'video/mpeg': '#mp4',
          'video/x-mpeg': '#mp4',
          // avi
          'application/x-troff-msvideo': '#avi',
          'video/avi': '#avi',
          'video/msvideo': '#avi',
          'video/x-msvideo': '#avi',
          // flash
          'application/x-shockwave-flash': '#fla'
        }
      }
    },
    computed: {
      currentChatRecord () {
        return this.$store.state.chatRecordList
      },
      fileServerUrl () {
        return this.$store.state.fileServerUrl
      },
      fileCompressUrl () {
        return this.$store.state.fileCompressUrl
      },
      apiServerUrl () {
        return this.$store.state.apiServerUrl
      },
      userId () {
        return window.localStorage.getItem('userId')
      },
      staffId () {
        return window.localStorage.getItem('staffId')
      },
      token () {
        return window.localStorage.getItem('token')
      },
      chatState () {
        return window.localStorage.getItem('chatState')
      },
      readyToSend () {
        for (let file of this.uploadList) {
          if (file.status !== 'finished') {
            return false
          }
        }
        return true
      },
      socket () {
        return this.$store.state.socket
      }
    },
    methods: {
      sendMessage () {
        if (!this.readyToSend) {
          this.$Notice.error({
            title: '文件传送完毕之前不可发送'
          })
          return
        }
        let sendMsg = this.inputText
        if (sendMsg === '' && this.uploadList.length === 0) {
          this.$Notice.warning({
            title: '不可以发送空消息！'
          })
          return
        }
        this.isScrollDown = true
        let time = this.getCurrentTime()
        // send text msg
        if (sendMsg !== '') {
          this.$store.commit({
            type: 'addChatRecord',
            content: {
              'from': this.userId,
              'to': this.staffId,
              'msg': sendMsg,
              'type': 'text',
              'time': time
              // 'hasSent': false
            }
          })
          this.socket.emit('userMsg', {
            staffId: this.staffId,
            userId: this.userId,
            token: this.token,
            msg: sendMsg,
            type: 'text'
          })
          // clear input
          this.inputText = ''
        } else {}
        // send files
        if (this.uploadList.length) {
          for (let index of this.uploadList.keys()) {
            let fileType = this.uploadList[index].response.type.startsWith('image') ? 'image' : 'file'
            // image
            if (fileType === 'image') {
              // get compressed image's url
              let fileUrl = this.uploadList[index].response.data
              let compressedUrl = ''
              axios.post(this.fileCompressUrl, {'fileUrl': fileUrl})
                .then(function (res) {
                  compressedUrl = res.data.data
                  // debug
//                  console.log('compressedUrl: ')
//                  console.log(compressedUrl)
                })
              compressedUrl = (compressedUrl.length > 0) ? compressedUrl : fileUrl
              this.$store.commit({
                type: 'addChatRecord',
                content: {
                  'from': this.userId,
                  'to': this.staffId,
                  'url': fileUrl,
                  'compressedUrl': compressedUrl,
                  'type': fileType,
                  'time': time
                  // 'hasSent': false
                }
              })
              // debug
              console.log('compressedurl: ' + compressedUrl)
              this.socket.emit('userMsg', {
                time: time,
                staffId: this.staffId,
                userId: this.userId,
                token: this.token,
                type: fileType,
                url: fileUrl,
                compressedUrl: compressedUrl
              })
            } else {
              // file
              this.$store.commit({
                type: 'addChatRecord',
                content: {
                  'from': this.userId,
                  'to': this.staffId,
                  'url': this.uploadList[index].response.data,
                  'name': this.uploadList[index].name,
                  'size': (this.uploadList[index].size > 1024) ? (this.uploadList[index].size >> 10) : 1,
                  'mimeType': this.uploadList[index].response.type,
                  'type': fileType,
                  'time': time
                  // 'hasSent': false
                }
              })
              this.socket.emit('userMsg', {
                time: time,
                staffId: this.staffId,
                userId: this.userId,
                token: this.token,
                type: fileType,
                url: this.uploadList[index].response.data,
                name: this.uploadList[index].name,
                size: (this.uploadList[index].size > 1024) ? (this.uploadList[index].size >> 10) : 1,
                mimeType: this.uploadList[index].response.type
              })
            }
          }
          // clear files
          this.$refs.upload.clearFiles()
          this.uploadList = this.$refs.upload.fileList
        }
      },
      zeroFill (num, size) {
        let s = '000000000' + num
        return s.substr(s.length - size)
      },
      getCurrentTime () {
        let curDate = new Date()
        return curDate.getFullYear() +
          '-' + this.zeroFill(curDate.getMonth() + 1, 2) +
          '-' + this.zeroFill(curDate.getDate(), 2) +
          ' ' + this.zeroFill(curDate.getHours(), 2) +
          ':' + this.zeroFill(curDate.getMinutes(), 2) +
          ':' + this.zeroFill(curDate.getSeconds(), 2)
      },
      scrollToBottom () {
        let el = document.getElementById('user-chat-content')
        el.scrollTop = el.scrollHeight
      },
      clickUploadFile () {
        this.$refs.uploadFile.click()
      },
      handleBeforeUpload (file) {
        const check = this.uploadList.length < 5
        if (!check) {
          this.$Notice.warning({
            title: '每次最多仅能传输5个文件'
          })
        }
        return check
      },
      handleMaxSize (file) {
        this.$Notice.warning({
          title: '超出上传文件大小限制',
          desc: '文件  ' + file.name + ' 过大，不应该超过 ' + this.maxFileSize + ' KB.'
        })
      },
      handleError (error, file) {
        this.$Notice.error({
          title: '文件 ' + file.name + ' 上传失败，请重试',
          desc: '错误原因：' + error
        })
      },
      handleSuccess (response, file) {
        this.$Notice.success({
          title: '文件 ' + file.name + ' 上传成功'
        })
        // debug
        console.log('uploadList length: ' + this.uploadList.length)
        console.log(this.uploadList)
      },
      handleRemove (file) {
        const fileList = this.$refs.upload.fileList
        this.$refs.upload.fileList.splice(fileList.indexOf(file), 1)
        this.$Notice.success({
          title: '文件 ' + file.name + ' 已从发送中清除'
        })
      },
      downloadFile () {
        // debug
        console.log('downloadFile')
      },
      showLocalFile () {
        // debug
        console.log('showLocalFile')
      },
      insertEmoji (emoji, event) {
        this.inputText += emoji.native
        console.log(this.inputText)
      },
      getFileIconName (fileName) {
        let array = fileName.split('.')
        let suffix = array[array.length - 1]
        let map = new Map([
          // compressed file
          ['zip', '#zip-1'],
          ['rar', '#zip-1'],
          ['7z', '#zip-1'],
          // image
          ['jpg', '#jpg'],
          ['jpeg', '#jpg'],
          ['png', '#png'],
          // word
          ['doc', '#doc'],
          ['docx', '#doc'],
          // powerpoint
          ['ppt', '#ppt'],
          ['pptx', '#ppt'],
          // excel
          ['xls', '#xls'],
          ['xlsx', '#xls'],
          // pdf
          ['pdf', '#pdf'],
          // exe
          ['exe', '#exe'],
          // psd
          ['psd', '#psd'],
          // txt
          ['txt', '#txt'],
          // html
          ['html', '#html'],
          ['htm', '#html'],
          // javascript
          ['js', '#javascript'],
          // css
          ['css', '#css'],
          // json
          ['json', '#json-file'],
          // mp3
          ['mp3', '#mp3'],
          // mp4
          ['mp4', '#mp4'],
          // avi
          ['avi', '#avi'],
          // flash
          ['swf', '#fla']
        ])
        let iconName = map.get(suffix)
        if (iconName) {
          return iconName
        } else {
          return '#file'
        }
      },
      showLargeImage (src) {
        // debug
        console.log('show large image, src: ' + src)
        this.showLargeImageModal = true
        this.largeImageSrc = src
      },
      screenShot () {
        const self = this
        setTimeout(function () {
          html2canvas(document.body).then(function (canvas) {
            let el = document.getElementById('screen-shot')
            while (el.lastChild) {
              el.removeChild(el.lastChild)
            }
            el.appendChild(canvas)
            self.showScreenShotModal = true
          })
        }, 1000)
      },
      sendScreenShot () {
        let canvas = document.getElementById('screen-shot').firstChild
        let self = this
        canvas.toBlob(function (blob) {
          let formData = new FormData()
          formData.append('file', blob)
          formData.append('fileType', 'image/png')
          formData.append('validTime', '1')
          let config = {
            headers: {
              'Content-Type': 'multipart/form-data'
            }
          }
          let fileUrl = ''
          let compressedUrl = ''
          let time = self.getCurrentTime()
          axios.post(self.fileServerUrl, formData, config)
            .then(function (res) {
              fileUrl = res.data.data
              if (fileUrl.length) {
                axios.post(self.fileCompressUrl, {'fileUrl': fileUrl})
                  .then(function (res) {
                    compressedUrl = res.data.data.length ? res.data.data : compressedUrl
                    self.currentChatRecord.push({
                      'from': self.userId,
                      'to': self.staffId,
                      'url': fileUrl,
                      'compressedUrl': compressedUrl,
                      'type': 'image',
                      'time': time
                    })
                    self.socket.emit('userMsg', {
                      time: time,
                      staffId: self.staffId,
                      userId: self.userId,
                      token: self.token,
                      type: 'image',
                      url: fileUrl,
                      compressedUrl: compressedUrl
                    })
                  })
              } else {
                self.showAlert('上传截图失败!')
              }
            })
        })
      },
      logout (event) {
        // TODO
        alert('你确定要离开吗？')
        event.preventDefault()
        this.socket.emit('userLogOut', {
          userId: this.userId,
          token: this.token
        })
        window.localStorage.clear()
      },
      getHistoryRecord () {
        this.isGettingHistoryRecord = true
        this.isScrollDown = false
        const self = this
        let currentIndex
        axios.get(this.apiServerUrl + '/user/chat-record', {
          params: {
            'userId': this.userId,
            'staffId': this.staffId,
            'index': -1
          }
        }).then(response => {
          let body = response.data.data
          if (body.length > 0) {
            currentIndex = body[0].index
            currentIndex -= self.currentChatRecord.length
            if (currentIndex < 0) {
              self.$Notice.success({
                title: '没有更多的消息了~'
              })
              self.isGettingHistoryRecord = false
              return
            }
            axios.get(self.apiServerUrl + '/user/chat-record', {
              params: {
                'userId': self.userId,
                'staffId': self.staffId,
                'index': currentIndex
              }
            }).then(response => {
              let body2 = response.data.data
              if (body2.length > 0) {
                let content = []
                for (let value of body2) {
                  let newMsg = value.content
                  // debug
                  if (value.direction === 's_u') {
                    newMsg.from = value.staffId
                    newMsg.to = value.userId
                  } else {
                    newMsg.from = value.userId
                    newMsg.to = value.staffId
                  }
                  // debug
                  console.log(newMsg)
                  content.unshift(newMsg)
                }
                this.$store.commit({
                  'type': 'prependChatRecord',
                  'content': content
                })
              }
              this.isGettingHistoryRecord = false
            }).catch(error => {
              this.$Notice.error({
                title: '获取历史消息失败，请稍后再试'
              })
              console.log(error)
              this.isGettingHistoryRecord = false
            })
          } else {
            this.$Notice.success({
              title: '没有更多的消息了~'
            })
            this.isGettingHistoryRecord = false
          }
        }).catch(error => {
          this.$Notice.error({
            title: '获取历史消息失败，请稍后再试'
          })
          console.log(error)
          this.isGettingHistoryRecord = false
        })
      }
    },
    created () {
      // if haven't login, show login page instead
      const self = this
      self.companyName = window.localStorage.getItem('companyName')
      let staffInfo = JSON.parse(window.localStorage.getItem('staffInfo'))
      // debug
      console.log(staffInfo)
      self.staffNickName = staffInfo.nickname
      self.staffPicUrl = staffInfo.picUrl
      self.staffRole = staffInfo.role
      // debug
      console.log('userId in user-chat: ' + self.userId)
      console.log('token in user-chat: ' + self.token)
      axios.get(self.$store.state.userLoginUrl, {
        params: {
          'userId': self.userId,
          'token': self.token
        }
      }).then(response => {
        let body = response.data.data
        if (body.code !== 0) {
          self.$router.push('login')
        }
      })
      // send userreg message
//      const io = require('socket.io-client')
//      this.socket = io(this.$store.state.socketIoServerUrl)
      // debug
      console.log(this)
      this.socket.emit('userReg', {userId: this.userId, token: this.token})
      // debug
      console.log('Sent userReg.')
      this.socket.on('regResult', (data) => {
        // debug
        console.log('Register result: code: ' + data['code'] + ', msg: ' + data['msg'])
      })
      this.socket.on('staffMsg', (data) => {
        if (data.type === 'snapshot') {
          this.showAskScreenShotModal = true
          return
        }
        let newMsg = {
          'time': data.time,
          'from': data.staffId,
          'to': data.userId,
          'type': data.type
        }
        if (data.type === 'text') {
          newMsg.msg = data.msg
        } else if (data.type === 'file') {
          newMsg.url = data.url
          newMsg.name = data.name
          newMsg.size = data.size
          newMsg.mimeType = data.mimeType
        } else {
          newMsg.url = data.url
          newMsg.compressedUrl = data.compressedUrl
        }
        self.$store.commit({
          type: 'addChatRecord',
          content: newMsg
        })
        // debug
        console.log(self.currentChatRecord)
      })
      this.socket.on('sendResult', (data) => {
        // debug
        console.log(data)
      })
      window.addEventListener('beforeunload', e => this.logout(e))
    },
    mounted () {
      this.uploadList = this.$refs.upload.fileList
      this.scrollToBottom()
    },
    updated () {
      if (this.isScrollDown) {
        this.scrollToBottom()
      }
    },
    beforeDestroyed () {
      window.removeEventListener('beforeunload', e => this.logout(e))
    }
  }
</script>
